import{_ as o,c as r,o as n,ag as s}from"./chunks/framework.Bw-5EFTY.js";const h=JSON.parse('{"title":"陰道鏡","description":"","frontmatter":{"title":"陰道鏡","updated":"2025-03-16T13:14:03.000Z","created":"2025-02-11T15:49:46.000Z"},"headers":[],"relativePath":"obstetrics-gynecology/GYN/一般婦科學/陰道鏡.md","filePath":"obstetrics-gynecology/GYN/一般婦科學/陰道鏡.md"}'),e={name:"obstetrics-gynecology/GYN/一般婦科學/陰道鏡.md"};function a(i,t,d,l,g,c){return n(),r("div",null,t[0]||(t[0]=[s('<p>陰道鏡 2025年2月11日 下午 03:49</p><h2 id="陰道鏡的適應症-indications" tabindex="-1">陰道鏡的適應症（Indications） <a class="header-anchor" href="#陰道鏡的適應症-indications" aria-label="Permalink to &quot;陰道鏡的適應症（Indications）&quot;">​</a></h2><p>陰道鏡主要用於評估 <strong>子宮頸病變</strong>，特別是 <strong>異常抹片結果</strong> 的患者，或 HPV 感染風險高的個案。</p><ul><li><strong>最常見的陰道鏡適應症是異常抹片</strong>（如 ASC-H, HSIL, AGC）</li><li><strong>高危 HPV（HPV 16, 18）陽性患者</strong> 需陰道鏡評估 | <strong>適應症</strong> | <strong>說明</strong> | |----|----| | <strong>異常子宮頸抹片（Pap smear）</strong> | LSIL、HSIL、ASC-H、AGC 需進一步評估 | | <strong>HPV 高危險型感染（High-risk HPV, HR-HPV）</strong> | HPV 16、18、其他高風險型持續感染 | | <strong>不明原因的異常子宮出血（AUB）</strong> | 特別是接觸性出血（Postcoital bleeding） | | <strong>可疑子宮頸病變（臨床可見異常）</strong> | 肉眼可見的病變，如潰瘍、腫塊或白斑 | | <strong>子宮頸癌前病變的追蹤</strong> | 例如 CIN 1、CIN 2、CIN 3 之後的監測 |</li></ul><h2 id="" tabindex="-1"><a class="header-anchor" href="#" aria-label="Permalink to &quot;&quot;">​</a></h2><h2 id="陰道鏡檢查的典型結果-findings-interpretation" tabindex="-1">陰道鏡檢查的典型結果（Findings &amp; Interpretation） <a class="header-anchor" href="#陰道鏡檢查的典型結果-findings-interpretation" aria-label="Permalink to &quot;陰道鏡檢查的典型結果（Findings &amp; Interpretation）&quot;">​</a></h2><table tabindex="0"><thead><tr><th><strong>陰道鏡所見</strong></th><th><strong>可能診斷</strong></th><th><strong>病變風險</strong></th></tr></thead><tbody><tr><td><strong>正常（No abnormal findings）</strong></td><td>正常子宮頸</td><td>低</td></tr><tr><td><strong>醋酸變白區（Acetowhite epithelium）</strong></td><td>CIN 1, HPV 感染</td><td>低至中等</td></tr><tr><td><strong>馬賽克樣（Mosaic pattern）</strong></td><td>CIN 2-3</td><td>中至高</td></tr><tr><td><strong>點狀血管（Punctation）</strong></td><td>CIN 2-3</td><td>高</td></tr><tr><td><strong>異常血管（Atypical vessels）</strong></td><td>侵襲性癌（Invasive cancer）</td><td>非常高</td></tr></tbody></table><h3 id="陰道鏡結果與-cin-分級" tabindex="-1">陰道鏡結果與 CIN 分級 <a class="header-anchor" href="#陰道鏡結果與-cin-分級" aria-label="Permalink to &quot;陰道鏡結果與 CIN 分級&quot;">​</a></h3><ul><li><strong>低度病變（LSIL, CIN 1）</strong>：多數可自行消退，1 年後追蹤</li><li><strong>高度病變（HSIL, CIN 2/3）</strong>：建議進一步處置（LEEP、錐切）</li></ul><h3 id="何時進行錐切-cone-biopsy-leep-國考熱區" tabindex="-1">何時進行錐切（Cone biopsy, LEEP）：國考熱區！ <a class="header-anchor" href="#何時進行錐切-cone-biopsy-leep-國考熱區" aria-label="Permalink to &quot;何時進行錐切（Cone biopsy, LEEP）：國考熱區！&quot;">​</a></h3><ul><li>CIN 2/3（高度病變）</li><li><strong>子宮頸內膜細胞異常（AGC, Atypical glandular cells）</strong></li><li>無法完整評估的轉化區（Type 3 TZ）</li><li>懷疑微侵襲性癌</li></ul><h2 id="陰道鏡切片-colposcopy-directed-biopsy" tabindex="-1">陰道鏡切片（Colposcopy-directed Biopsy） <a class="header-anchor" href="#陰道鏡切片-colposcopy-directed-biopsy" aria-label="Permalink to &quot;陰道鏡切片（Colposcopy-directed Biopsy）&quot;">​</a></h2><p><strong>適應症：</strong></p><ul><li>異常陰道鏡所見（醋酸白變、異常血管等）</li><li>高度抹片異常（HSIL、ASC-H、AGC）</li><li>HPV 16/18 陽性 <strong>常見病理診斷：</strong> | <strong>病理結果</strong> | <strong>對應陰道鏡結果</strong> | <strong>臨床處置</strong> | |---------------------------------|--------------------|------------------| | <strong>正常/慢性發炎</strong> | 無異常或輕微變化 | 追蹤 | | <strong>CIN 1（LSIL）</strong> | 輕度醋酸白變 | 觀察或冷凍治療 | | <strong>CIN 2/3（HSIL）</strong> | 馬賽克樣、點狀血管 | LEEP / 錐狀切除 | | <strong>子宮頸癌（Invasive Cancer）</strong> | 異常血管 | 進一步分期與治療 |</li></ul>',14)]))}const u=o(e,[["render",a]]);export{h as __pageData,u as default};
