import{_ as r,c as o,o as n,ag as s}from"./chunks/framework.Bw-5EFTY.js";const m=JSON.parse('{"title":"胚胎學","description":"","frontmatter":{"title":"胚胎學","updated":"2025-03-16T13:26:41.000Z","created":"2025-02-11T15:07:51.000Z"},"headers":[],"relativePath":"obstetrics-gynecology/胚胎解剖學/胚胎學.md","filePath":"obstetrics-gynecology/胚胎解剖學/胚胎學.md"}'),e={name:"obstetrics-gynecology/胚胎解剖學/胚胎學.md"};function a(l,t,g,d,i,c){return n(),o("div",null,t[0]||(t[0]=[s('<p>胚胎學 2025年2月11日 下午 03:07</p><p><strong>注意：極少考，建議不要花時間在此</strong></p><h2 id="受精與早期胚胎發育-fertilization-early-embryonic-development" tabindex="-1">受精與早期胚胎發育（Fertilization &amp; Early Embryonic Development） <a class="header-anchor" href="#受精與早期胚胎發育-fertilization-early-embryonic-development" aria-label="Permalink to &quot;受精與早期胚胎發育（Fertilization &amp; Early Embryonic Development）&quot;">​</a></h2><table><colgroup><col style="width:12%;"><col style="width:30%;"><col style="width:57%;"></colgroup><thead><tr class="header"><th><strong>時間點</strong></th><th><strong>發育階段</strong></th><th><strong>重點考點</strong></th></tr></thead><tbody><tr class="odd"><td><strong>0 小時</strong></td><td><strong>受精（Fertilization）</strong></td><td>- 發生在輸卵管 <strong>壺腹部（Ampulla）</strong><br> - 精子穿透透明帶（Zona pellucida），形成<strong>受精卵（Zygote）</strong></td></tr><tr class="even"><td><strong>24 小時內</strong></td><td><strong>卵裂（Cleavage）</strong></td><td>- <strong>不增加體積</strong>，快速分裂為桑葚胚（Morula, 16細胞）</td></tr><tr class="odd"><td><strong>3-4 天</strong></td><td><strong>囊胚（Blastocyst）</strong></td><td>- 形成內細胞群（ICM, 之後發育成胎兒）與滋養層（Trophoblast, 之後形成胎盤）</td></tr><tr class="even"><td><strong>6-7 天</strong></td><td><strong>著床（Implantation）</strong></td><td>- 囊胚附著子宮內膜，滋養層分化為<strong>細胞滋養層（Cytotrophoblast）</strong> &amp; <strong>合胞滋養層（Syncytiotrophoblast）</strong></td></tr><tr class="odd"><td><strong>8-12 天</strong></td><td><strong>雙胚層盤（Bilaminar disc）</strong></td><td>- 形成 <strong>上胚層（Epiblast）</strong> 與 <strong>下胚層（Hypoblast）</strong></td></tr><tr class="even"><td><strong>第三週</strong></td><td><strong>三胚層形成（Gastrulation）</strong></td><td>- 原條（Primitive streak）出現，形成<strong>外胚層、中胚層、內胚層</strong></td></tr></tbody></table><h3 id="胚胎學重要發育時間點" tabindex="-1">胚胎學重要發育時間點 <a class="header-anchor" href="#胚胎學重要發育時間點" aria-label="Permalink to &quot;胚胎學重要發育時間點&quot;">​</a></h3><ul><li><strong>受精</strong>：發生於 <strong>輸卵管壺腹部（Ampulla）</strong></li><li><strong>著床</strong>：第 <strong>6-7 天</strong> 由 <strong>囊胚（Blastocyst）</strong> 完成</li><li><strong>三胚層形成（Gastrulation）</strong>：<strong>第三週</strong></li><li><strong>神經管閉合（Neural tube closure）</strong>：<strong>第四週</strong></li></ul><h2 id="胎盤與胎膜-placenta-fetal-membranes" tabindex="-1">胎盤與胎膜（Placenta &amp; Fetal Membranes） <a class="header-anchor" href="#胎盤與胎膜-placenta-fetal-membranes" aria-label="Permalink to &quot;胎盤與胎膜（Placenta &amp; Fetal Membranes）&quot;">​</a></h2><table tabindex="0"><thead><tr><th><strong>結構</strong></th><th><strong>來源</strong></th><th><strong>功能</strong></th></tr></thead><tbody><tr><td><strong>胎盤（Placenta）</strong></td><td><strong>胎兒（滋養層）+ 母體（子宮內膜基底層）</strong></td><td><strong>氣體交換、營養供應、內分泌（hCG, hPL, Progesterone）</strong></td></tr><tr><td><strong>羊膜（Amnion）</strong></td><td><strong>內胚層衍生</strong></td><td><strong>形成羊水環境</strong></td></tr><tr><td><strong>絨毛膜（Chorion）</strong></td><td><strong>滋養層（Trophoblast）衍生</strong></td><td><strong>與子宮內膜形成胎盤</strong></td></tr></tbody></table><h3 id="性別分化" tabindex="-1">性別分化 <a class="header-anchor" href="#性別分化" aria-label="Permalink to &quot;性別分化&quot;">​</a></h3><ul><li><strong>AMH（Anti-Müllerian Hormone）</strong>：由 <strong>Sertoli cells</strong> 產生，抑制 Müllerian duct</li><li><strong>睪固酮</strong>（Testosterone）刺激 <strong>Wolffian duct</strong> 發育</li><li><strong>DHT（Dihydrotestosterone）</strong> 負責 <strong>男性外生殖器</strong>（陰莖、陰囊、攝護腺）</li></ul>',10)]))}const p=r(e,[["render",a]]);export{m as __pageData,p as default};
