import{_ as i,c as r,o as t,ag as o}from"./chunks/framework.Bw-5EFTY.js";const p=JSON.parse('{"title":"其他靜脈性疾病","description":"","frontmatter":{"title":"其他靜脈性疾病","updated":"2025-06-17T12:27:31.000Z","created":"2025-04-07T12:00:26.000Z"},"headers":[],"relativePath":"surgery/CVS/周邊血管疾病/其他靜脈性疾病.md","filePath":"surgery/CVS/周邊血管疾病/其他靜脈性疾病.md"}'),s={name:"surgery/CVS/周邊血管疾病/其他靜脈性疾病.md"};function n(e,l,u,a,g,h){return t(),r("div",null,l[0]||(l[0]=[o('<h1 id="表淺靜脈血栓性靜脈炎-superficial-vein-thrombophlebitis-svt" tabindex="-1">表淺靜脈血栓性靜脈炎（Superficial Vein Thrombophlebitis, SVT） <a class="header-anchor" href="#表淺靜脈血栓性靜脈炎-superficial-vein-thrombophlebitis-svt" aria-label="Permalink to &quot;表淺靜脈血栓性靜脈炎（Superficial Vein Thrombophlebitis, SVT）&quot;">​</a></h1><ul><li>定義 <ul><li>表淺靜脈的發炎及血栓形成，最常影響下肢表淺靜脈</li></ul></li><li>流行病學 <ul><li>5–40% 的 SVT 患者合併 DVT</li><li>25% 的患者合併 DVT 或肺栓塞</li></ul></li><li>分類 <ul><li><strong>遷移性血栓性靜脈炎 thrombophlebitis migrans</strong><ul><li>SVT 在正常表淺靜脈（沒有靜脈曲張）中反覆發作</li><li>與惡性腫瘤、血液、結締組織疾病有關</li><li><strong>Trousseau sign</strong>：與惡性腫瘤，特別是<strong>胰尾癌</strong>相關</li></ul></li><li><strong>Mondor disease</strong>：乳房的血栓性靜脈炎</li></ul></li><li>危險因子 <ul><li>靜脈曲張：最常發生於靜脈曲張的部位，但也可能出現在正常靜脈中</li><li>惡性腫瘤</li><li>血液、結締組織疾病</li><li>靜脈導管置放、藥物注射時造成外滲 <ul><li><strong>住院病患中 SVT 最常見原因</strong></li><li>**周邊中心靜脈導管（PICC）**的患者中，<strong>上肢靜脈血栓發生率高達38%</strong><ul><li><strong>頭靜脈（cephalic vein）</strong>：57%</li><li><strong>化膿性 SVT（suppurative SVT）</strong>：導管感染，可進展至敗血症</li></ul></li></ul></li><li>Behçet disease</li><li>血栓閉塞性脈管炎（Thromboangiitis obliterans）</li></ul></li><li>臨床表現 <ul><li>局部紅腫熱痛</li><li>皮膚紅腫硬化</li><li>條索狀血管（palpable cord） <ul><li>表淺靜脈路徑出現紅斑、壓痛與腫脹的線狀病灶</li><li><strong>隨時間靜脈會形成可觸及的纖維化條索</strong></li></ul></li><li>25% 的患者合併 DVT 或肺栓塞</li></ul></li><li>診斷 <ul><li>臨床診斷為主</li><li><strong>壓迫式超音波</strong><ul><li>懷疑有 DVT 或臨床表現不明確</li><li>觀察是否有表淺靜脈變厚、水腫、無法壓扁、有無腔內血栓、是否延伸到深層靜脈</li><li><strong>若沒有侵犯到深層靜脈侵犯，但有近端大隱靜脈SVT</strong><ul><li>建議5–7天後再次追蹤，因<strong>10–20%可能於1週內進展成DVT</strong></li></ul></li></ul></li></ul></li><li>治療 <ul><li><strong>保守治療</strong><ul><li>NSAIDs：止痛，也<strong>可降低SVT惡化或復發的風險</strong></li><li><strong>局部抗發炎藥膏</strong>：Indomethacin</li><li>穿彈性壓力襪</li></ul></li><li><strong>藥物治療</strong>：抗凝血治療 <ul><li><strong>治療時間</strong>：持續6週</li><li><strong>適應症</strong><ul><li>血栓長度 ≥ 5 公分</li><li>位置靠近深層靜脈</li><li>DVT 高風險</li></ul></li><li><strong>藥物</strong><ul><li>Rivaroxaban：口服 Xa inhibitor</li><li>Fondaparinux：可降低靜脈栓塞的發生率（達85%）</li><li>Low-molecular weight heparin, LMWH：Enoxaparin <ul><li>懷孕首選</li></ul></li></ul></li></ul></li><li><strong>特殊族群</strong><ul><li>SVT 延伸至股隱交界（saphenofemoral junction）1公分內 <ul><li>建議抗凝血治療6週，或結紮大隱靜脈</li></ul></li><li>化膿性 SVT：抗生素、移除導管 <ul><li>症狀嚴重時可考慮整段靜脈切除術</li></ul></li></ul></li></ul></li><li>併發症 <ul><li>深部靜脈栓塞</li><li>肺栓塞</li><li>感染：敗血性表淺血栓性靜脈炎（septic thrombophlebitis）</li></ul></li></ul><h1 id="" tabindex="-1"><a class="header-anchor" href="#" aria-label="Permalink to &quot;&quot;">​</a></h1><h1 id="腸系膜靜脈栓塞-mesenteric-vein-thrombosis" tabindex="-1">腸系膜靜脈栓塞（Mesenteric Vein Thrombosis） <a class="header-anchor" href="#腸系膜靜脈栓塞-mesenteric-vein-thrombosis" aria-label="Permalink to &quot;腸系膜靜脈栓塞（Mesenteric Vein Thrombosis）&quot;">​</a></h1><ul><li>流行病學 <ul><li>佔急性腸繫膜缺血（acute mesenteric ischemia）的 5–15%</li><li>死亡率可達 50%</li></ul></li><li>病因/危險因子 <ul><li>高凝血狀態（hypercoagulable state）</li><li>惡性腫瘤</li><li>門脈高壓（Portal hypertension）、肝硬化</li><li>雌激素治療（Estrogen therapy）：<strong>口服避孕藥、荷爾蒙替代療法</strong></li><li>腹腔鏡手術後的罕見併發症</li></ul></li><li>臨床表現 <ul><li>腹痛與腹脹：最常見</li><li>噁心、嘔吐、腹瀉</li><li>Peritoneal signs：<strong>少於一半</strong>的病人，若陽性可能已進展至腸梗塞、壞死</li></ul></li><li>診斷 <ul><li><strong>電腦斷層（Contrast CT）、核磁共振（MRI）</strong><ul><li><strong>敏感度：100%；特異度：98%</strong></li><li><strong>MRI 的準確率跟 CT 差不多</strong></li></ul></li><li><strong>超音波（Ultrasound）</strong> ：輔助工具 <ul><li>敏感度：93%；特異度：99%</li></ul></li></ul></li><li>治療 <ul><li><strong>初始處置</strong>： <ul><li>給水（fluid resuscitation）</li><li>IV 注射 Heparin</li><li>bowel rest</li></ul></li><li><strong>若狀況有改善</strong><ul><li>可慢慢恢復用口進食</li><li>3-4天內慢慢轉為口服抗凝血藥 <ul><li><strong>抗凝血治療時間</strong><ul><li>3–6個月</li><li>若為<strong>永久性高凝血狀態，需終生使用</strong></li></ul></li></ul></li></ul></li><li><strong>手術</strong><ul><li>切除壞死的腸段 <ul><li><strong>若殘餘腸段血流不確定</strong>，建議 24–48小時內再手術評估（second-look operation）</li><li>切除後再進行腸段吻合（primary anastomosis）</li><li><strong>術中與術後需給予廣效性抗生素</strong></li></ul></li><li><strong>適應症</strong><ul><li>Peritoneal signs (+)：需進行緊急剖腹探查（urgent laparotomy）</li></ul></li><li><strong>手術Findings</strong><ul><li>腸繫膜與腸壁腫脹、發紺（cyanosis）</li><li>靜脈血栓可能延伸至遠端腸繫膜靜脈</li><li><strong>動脈供應通常仍保留</strong></li></ul></li></ul></li></ul></li></ul>',5)]))}const m=i(s,[["render",n]]);export{p as __pageData,m as default};
