import{_ as r,c as d,o as n,ag as s}from"./chunks/framework.Bw-5EFTY.js";const p=JSON.parse('{"title":"編輯格式","description":"","frontmatter":{"title":"編輯格式","updated":"2025-04-24T00:31:43.000Z","created":"2025-04-24T00:18:23.000Z"},"headers":[],"relativePath":"internal-medicine/胸內/編輯格式.md","filePath":"internal-medicine/胸內/編輯格式.md"}'),o={name:"internal-medicine/胸內/編輯格式.md"};function g(e,t,a,i,l,h){return n(),d("div",null,t[0]||(t[0]=[s('<p>AM</p><ul><li>編輯格式 <ul><li>顏色註記方式 <ul><li>重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記</li><li>重要術式治療藥物 : <strong>綠色標記+粗體</strong></li><li>重要內文 : 紅色(標準色彩)/<strong>橘色+粗體(橘色那一欄第一個)</strong>(如果同色太醜就紅橘相間)</li><li>直接來複製顏色格式一定不會錯xd</li></ul></li></ul></li><li>基本概念 | <strong>分類</strong> | <strong>定義</strong> | |-------------|--------------------------------------------------| | Obstructive | 空氣「吐不出去」：氣道阻塞使得呼氣變慢、氣體滯留 | | Restrictive | 空氣「進不來」：肺部無法完全擴張，總容量下降 |</li></ul><p><strong>肺功能指標比較（PFT）</strong></p><table tabindex="0"><thead><tr><th><strong>指標</strong></th><th><strong>Obstructive</strong></th><th><strong>Restrictive</strong></th></tr></thead><tbody><tr><td><strong>FEV1</strong></td><td>↓↓↓</td><td>↓</td></tr><tr><td><strong>FVC</strong></td><td>↓ / 正常</td><td>↓↓↓</td></tr><tr><td><strong>FEV1/FVC</strong></td><td><strong>↓ &lt; 70%</strong>（關鍵）</td><td><strong>正常或↑ &gt; 80%</strong></td></tr><tr><td><strong>TLC</strong></td><td>正常或↑（氣體滯留）</td><td><strong>↓↓</strong></td></tr><tr><td><strong>RV / FRC</strong></td><td>↑↑（air trapping）</td><td>↓</td></tr><tr><td><strong>DLCO</strong></td><td>正常或↓（如 COPD）</td><td><strong>↓（ILD 明顯下降）</strong></td></tr></tbody></table><p><strong>代表性疾病</strong></p><table tabindex="0"><thead><tr><th><strong>分類</strong></th><th><strong>常見疾病</strong></th><th><strong>特徵</strong></th></tr></thead><tbody><tr><td>Obstructive</td><td><strong>Asthma、COPD（chronic bronchitis + emphysema）</strong>、Bronchiectasis</td><td>可逆性（Asthma）、氣體滯留（emphysema）</td></tr><tr><td>Restrictive</td><td><strong>ILD（IPF, NSIP）、胸壁疾病、神經肌肉病變（ALS）</strong></td><td>彈性下降、肺順應性低</td></tr></tbody></table><p><strong>擴散能力 DLCO 判讀重點</strong></p><table tabindex="0"><thead><tr><th><strong>狀況</strong></th><th><strong>DLCO變化</strong></th><th><strong>原因</strong></th></tr></thead><tbody><tr><td><strong>ILD、肺纖維化</strong></td><td>↓</td><td>擴散距離變長、毛細血管破壞</td></tr><tr><td><strong>Emphysema（COPD）</strong></td><td>↓</td><td>肺泡壁破壞、表面積減少</td></tr><tr><td><strong>Pulmonary embolism、肺高壓</strong></td><td>↓</td><td>血流灌流異常</td></tr><tr><td><strong>Anemia</strong></td><td>↓</td><td>攜氧載體（Hb）少，需校正 DLCO</td></tr><tr><td><strong>Asthma</strong></td><td>正常或↑</td><td>肺泡保留，灌流正常甚至增加（急性期）</td></tr></tbody></table><p><strong>病因分類提示（特別適用於限制型）</strong></p><table tabindex="0"><thead><tr><th><strong>分類</strong></th><th><strong>原因示意</strong></th></tr></thead><tbody><tr><td><strong>肺實質病變</strong></td><td>ILD（最典型）、ARDS、藥物、放射線</td></tr><tr><td><strong>胸壁異常</strong></td><td>肋骨骨折、胸廓發育異常</td></tr><tr><td><strong>神經肌肉疾病</strong></td><td>ALS、Myasthenia gravis、Guillain-Barré syndrome</td></tr><tr><td><strong>腹部壓迫</strong></td><td>肥胖、腹水、妊娠晚期</td></tr></tbody></table>',10)]))}const b=r(o,[["render",g]]);export{p as __pageData,b as default};
