import{_ as i,c as l,o as e,ag as n}from"./chunks/framework.Bw-5EFTY.js";const P=JSON.parse('{"title":"Interstitial lung disease","description":"","frontmatter":{"title":"Interstitial lung disease","updated":"2025-05-14T21:22:16.000Z","created":"2025-04-23T15:25:54.000Z"},"headers":[],"relativePath":"internal-medicine/胸內/限制性肺疾病/Interstitial lung disease.md","filePath":"internal-medicine/胸內/限制性肺疾病/Interstitial lung disease.md"}'),s={name:"internal-medicine/胸內/限制性肺疾病/Interstitial lung disease.md"};function o(a,t,r,d,c,p){return e(),l("div",null,t[0]||(t[0]=[n('<p>Interstitial lung disease Wednesday, April 23, 2025 3:25 PM</p><ul><li><p>編輯格式</p><ul><li>顏色註記方式 <ul><li>重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記</li><li>重要術式治療藥物 : <strong>綠色標記+粗體</strong></li><li>重要內文 : 紅色(標準色彩)/<strong>橘色+粗體(橘色那一欄第一個)</strong>(如果同色太醜就紅橘相間)</li><li>直接來複製顏色格式一定不會錯xd</li></ul></li></ul></li><li><p>定義與分類（Definition &amp; Classification）</p><ul><li>ILD是一群影響肺泡間質（interstitium）的疾病總稱，超過200種，常見表現為纖維化與慢性發炎。傳統分類方式如下： <ul><li>類別分類 | 類別 | 疾病舉例 | |----|----| | 已知原因 | 吸入性（asbestosis、silicosis）、藥物（amiodarone、methotrexate）、放射線、CTD相關 | | 未知原因 | Idiopathic interstitial pneumonias（IPF、NSIP、COP、AIP 等） | | 肉芽腫型 | Sarcoidosis、Hypersensitivity pneumonitis | | 其他特殊型 | LAM、Pulmonary Langerhans cell histiocytosis、Alveolar proteinosis |</li></ul></li></ul></li><li><p>臨床表現與病史詢問（Clinical Presentation &amp; History） | 項目 | 特徵 | |----|----| | 呼吸道症狀 | 漸進性呼吸困難（最常見）、乾咳 | | 其他症狀 | 疲倦、體重減輕；部分有胸痛（sarcoidosis）、血痰（vasculitis） | | 身體檢查 | 基底部乾囉音（fine crackles）、杵狀指、晚期 cyanosis | | 危險因子 | 吸菸史、職業暴露、既往CTD或惡性腫瘤、藥物史（如nitrofurantoin） | | 家族史 | Idiopathic pulmonary fibrosis 有家族聚集現象，MUC5B 基因變異與 telomere genes 相關 |</p></li><li><p>常見類型概覽</p></li></ul><table><colgroup><col style="width:11%;"><col style="width:23%;"><col style="width:21%;"><col style="width:26%;"><col style="width:17%;"></colgroup><thead><tr class="header"><th>疾病</th><th>全名</th><th>特徵症狀</th><th>HRCT影像</th><th>預後</th></tr></thead><tbody><tr class="odd"><td>IPF</td><td>Idiopathic Pulmonary Fibrosis</td><td>老年男性、乾咳、喘</td><td>basilar reticular opacities + honeycombing（UIP pattern）</td><td>預後差（3–5年存活率50%）</td></tr><tr class="even"><td>NSIP</td><td>Non-Specific Interstitial Pneumonia</td><td>年輕女性、CTD共病</td><td>symmetric GGO + reticulation（無honeycomb）</td><td>預後佳，特別是cellular型</td></tr><tr class="odd"><td>RB-ILD / DIP</td><td><p>Respiratory Bronchiolitis-Associated ILD/</p><p>Desquamative Interstitial Pneumonia</p></td><td>吸菸者、中央小葉結節</td><td>GGO、支氣管壁增厚</td><td>DIP GGO明顯，預後好</td></tr><tr class="even"><td>COP</td><td>Cryptogenic Organizing Pneumonia</td><td>感冒樣、亞急性病程</td><td>migratory consolidation ± GGO、反轉光暈</td><td>可逆，需長期類固醇</td></tr><tr class="odd"><td>AIP</td><td>Acute Interstitial Pneumonia</td><td>急性呼吸衰竭</td><td>bilateral GGO ± consolidation（DAD）</td><td>預後極差</td></tr><tr class="even"><td>LIP</td><td>Lymphoid Interstitial Pneumonia</td><td>少見，與 HIV、Sjögren&#39;s syndrome 有關</td><td>Bilateral GGO + 囊狀變化（thin-walled cysts）+多發小結節</td><td>多為慢性、進展緩慢，可穩定多年</td></tr></tbody></table><ul><li><p>診斷策略（Diagnostic Approach）</p><ul><li>呼吸功能測試（PFT） <ul><li>限制型通氣障礙（TLC ↓，FEV1/FVC 正常或↑）</li><li><strong>DLCO下降為早期變化</strong></li></ul></li><li><strong>影像學檢查</strong> | 工具 | 重點 | |----------|-------------------------------------------------| | X光 | 低敏感，提示性變化（reticulation, volume loss） | | <strong>HRCT</strong> | <strong>診斷核心（UIP、NSIP、mosaic、honeycombing）</strong> |</li></ul></li><li><p>血液與免疫檢查</p><ul><li>CTD autoantibodies（ANA, RF, anti-CCP, anti-Scl70, anti-Jo1）</li><li>Precipitin（如HP抗體）</li></ul></li><li><p>支氣管鏡/肺切片 | 工具 | 用途 | |-------------|-------------------------------------------------| | BAL | 細胞學（↑lymphocyte in HP, eosinophilia in EP） | | TBLB / VATS | 確診NSIP、UIP、COP；VATS yield 高但具風險 |</p></li><li><p>治療策略（Treatment） | 類型 | 治療建議 | |--------------|-------------------------------------------------------| | IPF | <strong>抗纖維化：Pirfenidone、Nintedanib</strong>；不可用steroids | | NSIP | <strong>類固醇 ± mycophenolate、azathioprine、rituximab</strong> | | COP | <strong>類固醇首選</strong>，療程6個月以上，易復發 | | AIP / AE-IPF | 支持療法 ± 類固醇（證據弱） | | <strong>肺移植</strong> | <strong>嚴重進展者為終極療法</strong> |</p></li><li><p>重點提示</p><ul><li>UIP特徵（honeycombing + reticulation + basilar predilection）幾乎可診斷IPF</li><li>ILD 最常見症狀為 exertional dyspnea 與 dry cough</li><li>Pirfenidone/Nintedanib 可延緩IPF惡化，並改善存活率</li></ul></li></ul><p>REFERENCES</p><ul><li>Harrisons</li><li>Boards and beyonds</li></ul>',6)]))}const g=i(s,[["render",o]]);export{P as __pageData,g as default};
