import{_ as i,c as e,o as a,ag as t}from"./chunks/framework.Bw-5EFTY.js";const m=JSON.parse('{"title":"頭部創傷/顱內出血","description":"","frontmatter":{"title":"頭部創傷/顱內出血","updated":"2025-05-25T10:12:58.000Z","created":"2025-04-04T20:23:01.000Z"},"headers":[],"relativePath":"surgery/NS/頭部創傷顱內出血.md","filePath":"surgery/NS/頭部創傷顱內出血.md"}'),r={name:"surgery/NS/頭部創傷顱內出血.md"};function u(n,l,o,s,_,d){return a(),e("div",null,l[0]||(l[0]=[t("<p>頭部創傷/顱內出血 2025年4月4日 下午 08:23</p><ul><li>定義 <ul><li>腦震盪(Concussion)： <ul><li>頭骨和腦沒有受傷 = 沒有image finding</li><li>有意識變化：意識不清、失去意識、記憶缺失(Amnesia)</li><li>嚴重度評估：Colorado分級系統(最常用) <ul><li>Grade 1(Mild): <ul><li>意識錯亂但仍有意識</li></ul></li><li>Grade 2(Moderate) <ul><li>意識錯亂但仍有意識</li><li>創傷後失憶(Post-traumatic amnesia)</li></ul></li><li>Grade 3(Severe): <ul><li>任何程度的意識喪失</li></ul></li></ul></li><li>受傷後腦部表現： <ul><li>維持高代謝狀態1週</li><li>1-2週內對傷害更敏感(輕微頭部創傷後也可能出現記憶困難、頭痛=second-impact syndrome)</li></ul></li></ul></li><li>挫傷(Contusion)： <ul><li>有異常image finding：頭骨骨折、顱內出血、水腫…</li><li>Countercoup injury：受撞擊的對側受傷</li></ul></li><li>Diffuse axonal injury：頭部受撞擊時因加、減速過程受到機械性損傷，使神經元軸突分裂</li><li>CT看起來可能是正常的</li><li>依嚴重程度不同可能會意識混亂、失去意識甚至死亡</li></ul></li></ul>",2)]))}const p=i(r,[["render",u]]);export{m as __pageData,p as default};
